    return curBuf.get();
  } catch (BufferUnderflowException e) {
    do {
      curBufIndex++;
      if (curBufIndex >= buffers.length)
        throw new IOException("read past EOF");
      curBuf = buffers[curBufIndex];
      curBuf.position(0);
    } while (!curBuf.hasRemaining());
    return curBuf.get();
  }
}

