@Override
public void execute(final Runnable command) {
  try {
    semaphore.acquire();
  } catch (InterruptedException e1) {
    throw new RuntimeException();
  }
  try {
    super.execute(new Runnable() {
      public void run() {
        try {
          command.run();
        } finally {
          semaphore.release();
        }
      }
    });
  } catch (RejectedExecutionException e) {
    semaphore.release();
    throw e;
  }
}

