public void closeSearcher() {
  log.info("Closing main searcher on request.");
  synchronized (searcherLock) {
    if (_searcher != null) {
      _searcher.decref();   // dec refcount for this._searcher
      _searcher=null;
      SolrInfoRegistry.getRegistry().remove("currentSearcher");
    }
  }
}

