/* handle -ness */
private void nessEndings() {
  if (endsIn('n', 'e', 's', 's')) { /*
                                     * this is a very productive endings, so
                                     * just accept it
                                     */
    word.setLength(j + 1);
    k = j;
    if (word.charAt(j) == 'i') word.setCharAt(j, 'y');
    lookup();
  }
  return;
}

