protected String getSysErr() {
  Assert.assertTrue(suppressOutputStreams);
  System.err.flush();
  try {
    return new String(syserr.toByteArray(), "UTF-8");
  } catch (UnsupportedEncodingException e) {
    throw new RuntimeException(e);
  }
}

