@Override
public void close() {
  if (closed) {
    return;
  }
  log.debug("Closing " + hashCode());
  closed = true;
  backends.forEach((p, b) -> IOUtils.closeQuietly(b));
  backends.clear();
  syncService.shutdownNow();
  syncService = null;
}

