static void encodeFlags(BytesRef b, char flags[]) {
  int len = flags.length << 1;
  b.grow(len);
  b.length = len;
  int upto = b.offset;
  for (int i = 0; i < flags.length; i++) {
    int flag = flags[i];
    b.bytes[upto++] = (byte) ((flag >> 8) & 0xff);
    b.bytes[upto++] = (byte) (flag & 0xff);
  }
}
