// Remove the root Scorer from subScorers and re-establish it as a heap
private void heapRemoveRoot() {
  if (numScorers == 1) {
    subScorers[0] = null;
    numScorers = 0;
  } else {
    subScorers[0] = subScorers[numScorers - 1];
    subScorers[numScorers - 1] = null;
    --numScorers;
    heapAdjust(0);
  }
}

