@AfterClass
public static void teardownClass() throws Exception {
  try {
    shutdownCluster();
  } finally {
    try {
      HdfsTestUtil.teardownClass(dfsCluster);
    } finally {
      dfsCluster = null;
      System.clearProperty("solr.hdfs.home");
      System.clearProperty("solr.hdfs.blockcache.enabled");
    }
  }
}

