public TimerContext time(String operation) {
  String op = operation.toLowerCase(Locale.ROOT);
  Stat stat = stats.get(op);
  if (stat == null) {
    stat = new Stat();
    stats.put(op, stat);
  }
  return stat.requestTime.time();
}

public TimerContext time(String operation) {
  String op = operation.toLowerCase(Locale.ROOT);
  Stat stat;
  synchronized (stats) {
    stat = stats.get(op);
  if (stat == null) {
    stat = new Stat();
    stats.put(op, stat);
  }
  }
  return stat.requestTime.time();
}

