/**
 * Finds the largest entry whose value is less than or equal to c, or 0 if
 * there is no such entry.
 */
static int findIndex(int c, int[] points) {
  int a = 0;
  int b = points.length;
  while (b - a > 1) {
    int d = (a + b) >>> 1;
    if (points[d] > c) b = d;
    else if (points[d] < c) a = d;
    else return d;
  }
  return a;
}

