@Override
public boolean useCompoundFile(SegmentInfos infos, SegmentInfoPerCommit mergedInfo) throws IOException {
  final boolean doCFS;

  if (!useCompoundFile) {
    doCFS = false;
  } else if (noCFSRatio == 1.0) {
    doCFS = true;
  } else {
    long totalSize = 0;
    for (SegmentInfoPerCommit info : infos) {
      totalSize += size(info);
    }

    doCFS = size(mergedInfo) <= noCFSRatio * totalSize;
  }
  return doCFS;
}

