public Acceptor(ServerSocket serverSocket, URI uri) {
  socket = serverSocket;
  target = uri;
  pause.set(new CountDownLatch(0));
  try {
    socket.setSoTimeout(ACCEPT_TIMEOUT_MILLIS);
  } catch (SocketException e) {
    e.printStackTrace();
  }
}

