     valSrcScorers[i] = valSrcWeights[i].scorer(context, acceptDocs);
  }
  return new CustomScorer(CustomScoreQuery.this.getCustomScoreProvider(context), this, queryWeight, subQueryScorer, valSrcScorers);
}

