@Override
public int[] grow() {
  assert address != null;
  final int oldSize = address.length;
  final int[] retVal = super.grow();
  address = ArrayUtil.grow(address, retVal.length);
  bytesUsed.addAndGet(RamUsageEstimator.NUM_BYTES_INT
      * (address.length - oldSize));
  return retVal;
}

