/**
 * Checks if character is a vowel..
 * Creation date: (16/03/2002 10:47:03 PM)
 * @return boolean
 * @param letter char
 */
private boolean isVowel(char letter)
{
    for (int i = 0; i < vowels.length; i++)
    {
        if (letter == charset[vowels[i]])
            return true;
    }
    return false;
}

