void doSnapPull() {
  if (!isSlave)
    return;
  if (snapPullLock.isLocked())
    return;
  snapPullLock.lock();
  try {
    snapPuller.fetchLatestIndex(core);
  } catch (Exception e) {
    LOG.error("SnapPull failed ", e);
  } finally {
    snapPullLock.unlock();
  }
}

