public void generate(IndexReader reader, TermEnum enumerator) throws IOException {
  TermDocs termDocs = reader.termDocs();
  try {
    do {
      Term term = enumerator.term();
      if (term == null)
        break;
      mtq.numberOfTerms++;
      termDocs.seek(term);
      while (termDocs.next()) {
        handleDoc(termDocs.doc());
      }
    } while (enumerator.next());
  } finally {
    termDocs.close();
  }
}

