public static String tsToString(TokenStream in) throws IOException {
  StringBuffer out = new StringBuffer();
  Token t = in.next();
  if (null != t)
    out.append(t.termText());

  for (t = in.next(); null != t; t = in.next()) {
    out.append(" ").append(t.termText());
  }
  in.close();
  return out.toString();
}

