private static int typeToSize(Type type) {
  switch (type) {
  case FIXED_INTS_16:
    return 2;
  case FIXED_INTS_32:
    return 4;
  case FIXED_INTS_64:
    return 8;
  case FIXED_INTS_8:
    return 1;
  default:
    throw new IllegalStateException("illegal type " + type);
  }
}

