  int endPlace = end - 1;

  select(list, middlePlace, beginPlace, endPlace);
  distributeAndFind(list, percentiles, beginIdx, middleIdxb - 1);
  distributeAndFind(list, percentiles, middleIdxe + 1, endIdx);
