private void pushTop() throws IOException {
  // call next() on each top, and reorder queue
  for (int i = 0; i < numTop; i++) {
    TermsEnumWithSlice top = queue.top();
    top.current = top.terms.next();
    if (top.current == null) {
      queue.pop();
    } else {
      queue.updateTop();
    }
  }
  numTop = 0;
}

