/*
 * called after a close to restart the acceptor on the same port
 */
public void reopen() {
  log.info("Re-opening connectivity to "+getUrl());
  try {
    open();
  } catch (Exception e) {
    log.debug("exception on reopen url:" + getUrl(), e);
  }
}

