protected void collectFiles(File f, ArrayList inputFiles) {
  if (!f.canRead()) {
    return;
  }
  if (f.isDirectory()) {
    File files[] = f.listFiles();
    for (int i = 0; i < files.length; i++) {
      collectFiles(files[i],inputFiles);
    }
    return;
  }
  inputFiles.add(f);
  addUniqueBytes(f.length());
}

