@Override
public void consume(BytesRef ref, int ord, long offset) throws IOException {
  pagedBytes.copy(ref);
  if (ordToOffset != null) {
    if (ord+1 >= ordToOffset.length) {
      ordToOffset = ArrayUtil.grow(ordToOffset, ord + 2);
    }
    ordToOffset[ord+1] = offset;
  }
}

