// nocommit: remove deletions from SR
void doCommit() throws IOException {
  assert hasChanges;
  startCommit();
  boolean success = false;
  try {
    commitChanges();
    success = true;
  } finally {
    if (!success) {
      rollbackCommit();
    }
  }
}

