public boolean nextDoc() throws IOException {
  if (freq.bufferOffset + freq.upto == freq.endIndex) {
    if (p.lastDocCode != -1) {
      // Return last doc
      docID = p.lastDocID;
      termFreq = p.docFreq;
      p.lastDocCode = -1;
      return true;
    } else 
      // EOF
      return false;
  }

  final int code = freq.readVInt();
  docID += code >>> 1;
  if ((code & 1) != 0)
    termFreq = 1;
  else
    termFreq = freq.readVInt();

  return true;
}

