  Relation r = poly.relate(minLat, maxLat, minLon, maxLon);
  if (queryRelation == QueryRelation.DISJOINT) {
    return r == Relation.CELL_OUTSIDE_QUERY;
  }
  return r != Relation.CELL_OUTSIDE_QUERY;
