void launchThreads(final int numIter) {
  for (int i = 0; i < NUM_THREADS; i++) {
    threads[i] = new Thread() {
      @Override
      public void run() {
        try {
          Term term = getDeleteTerm();
          mainWriter.deleteDocuments(term);
          synchronized (deletedTerms) {
            deletedTerms.add(term);
          }
        } catch (Throwable t) {
          handle(t);
        }
      }
    };
  }
}

