private HashMap getScores (Hits hits)
throws IOException {
	HashMap scoreMap = new HashMap();
	int n = hits.length();
	for (int i=0; i<n; ++i) {
		Document doc = hits.doc(i);
		String[] v = doc.getValues("tracer");
		assertEquals (v.length, 1);
		scoreMap.put (v[0], new Float(hits.score(i)));
	}
	return scoreMap;
}

