@Override
void notifyReaderClosedListeners(Throwable th) throws IOException {
  synchronized(readerClosedListeners) {
    for(ClosedListener listener : readerClosedListeners) {
      try {
        listener.onClose(cacheHelper.getKey());
      } catch (Throwable t) {
        if (th == null) {
          th = t;
        } else {
          th.addSuppressed(t);
        }
      }
    }
    IOUtils.reThrow(th);
  }
}

