private void loadModel() throws IOException {
  InputStream is = null;
  try{
    if (modelFile != null) {
      is = solrResourceLoader.openResource(modelFile);
      model = new LanguageDetectorModel(is);
    }
  }
  finally{
    IOUtils.closeQuietly(is);
  }
}

