  addDoc("JK Rowling", w);
  addDoc("Joanne K Roling", w);
  addDoc("Bruce Willis", w);
  addDoc("Willis bruce", w);
  addDoc("Brute willis", w);
  addDoc("B. willis", w);
  IndexReader r = w.getReader();
  w.close();

  Query q = new QueryParser(Version.LUCENE_CURRENT, "field", analyzer).parse( "giga~0.9" );

