/**
 * Third step of the Porter Algorithm<br>
 * refer to http://snowball.sourceforge.net/french/stemmer.html for an explanation
 */
private void step3() {
	if (sb.length()>0)
	{
		char ch = sb.charAt( sb.length()-1 );
		if (ch == 'Y')
		{
			sb.setCharAt( sb.length()-1, 'i' );
			setStrings();
		}
		else if (ch == 'รง')
		{
			sb.setCharAt( sb.length()-1, 'c' );
			setStrings();
		}
	}
}

