private NamedList<Object> makeRequest(String baseUrl, SolrRequest request)
    throws SolrServerException, IOException {
  SolrClient client = createNewSolrClient("", baseUrl);
  try {
    return client.request(request);
  } finally {
    client.shutdown();
  }
}

