private final int binarySearch(int hi, int low, int target, int[] docs) {
  while (low <= hi) {
    int mid = (hi + low) >>> 1;
    int doc = docs[mid];
    if (doc < target) {
      low = mid + 1;
    } else if (doc > target) {
      hi = mid - 1;
    } else {
      low = mid;
      break;
    }
  }
  return low-1;
}

