@Override
@After
public void tearDown() throws Exception {
  if (!proxies.isEmpty()) {
    for (SocketProxy proxy : proxies.values()) {
      proxy.close();
    }
  }

  System.clearProperty("numShards");

  try {
    super.tearDown();
  } catch (Exception exc) {}

  resetExceptionIgnores();
}

