@Override
public long readVLong() {
  assert checkBounds();
  byte b = bytes[pos++];
  long i = b & 0x7F;
  for (int shift = 7; (b & 0x80) != 0; shift += 7) {
    assert checkBounds();
    b = bytes[pos++];
    i |= (b & 0x7FL) << shift;
  }
  return i;
}

