  b = bytes[pos++];
  i |= (b & 0x7FL) << 28;
  if ((b & 0x80) == 0) return i;
  assert checkBounds();
  b = bytes[pos++];
  i |= (b & 0x7FL) << 35;
  if ((b & 0x80) == 0) return i;
  assert checkBounds();
  b = bytes[pos++];
  i |= (b & 0x7FL) << 42;
  if ((b & 0x80) == 0) return i;
  assert checkBounds();
  b = bytes[pos++];
  i |= (b & 0x7FL) << 49;
  if ((b & 0x80) == 0) return i;
