// TODO: override bulk read, for better perf
@Override
public int nextDoc() {
  while(true) {
    upto++;
    if (upto < ramTerm.docs.size()) {
      current = ramTerm.docs.get(upto);
      if (liveDocs == null || liveDocs.get(current.docID)) {
        posUpto = 0;
        return current.docID;
      }
    } else {
      return NO_MORE_DOCS;
    }
  }
}

