private void close() throws IOException {
  boolean success = false;
  try {
    IOUtils.close(reader);
    success = true;
  } finally {
    if (success) {
      IOUtils.deleteFilesIfExist(tempInput, tempSorted);
    } else {
      IOUtils.deleteFilesIgnoringExceptions(tempInput, tempSorted);
    }
  }
}

