public void testStartOfCharReverseSkip() throws Exception {
  String path = "a/b/c";
  ReversePathHierarchyTokenizer t = new ReversePathHierarchyTokenizer( new StringReader(path), 1 );
  assertTokenStreamContents(t,
      new String[]{"a/b/", "b/"},
      new int[]{0, 2},
      new int[]{4, 4},
      new int[]{1, 0},
      path.length());
}

