PendingDeletes(SegmentReader reader, SegmentCommitInfo info) {
  this.info = info;
  liveDocsShared = true;
  liveDocs = reader != null ? reader.getLiveDocs() : null;
  if (reader != null) {
    pendingDeleteCount = reader.numDeletedDocs() - info.getDelCount();
  } else {
    pendingDeleteCount = 0;
  }
}

