          } finally {
            if (asyncSolrCoreLoad) {
              solrCores.markCoreAsNotLoading(cd);
            }
          }
          try {
            zkSys.registerInZk(core, true, false);
          } catch (RuntimeException e) {
            SolrException.log(log, "Error registering SolrCore", e);
          }
          return core;
        }));
      }
    }


