    backgroundCloser = new CloserThread(this, solrCores, cfg);
    backgroundCloser.start();

  } finally {
    if (asyncSolrCoreLoad && futures != null) {

      coreContainerWorkExecutor.submit(() -> {
        try {
          for (Future<SolrCore> future : futures) {
            try {
              future.get();
