public Token next() throws IOException {
  if (!done) {
    done = true;
    StringBuffer buffer = new StringBuffer();
    int length;
    while (true) {
      length = input.read(this.buffer);
      if (length == -1) break;

      buffer.append(this.buffer, 0, length);
    }
    String text = buffer.toString();
    return new Token(text, 0, text.length());
  }
  return null;
}

