/** Used for debugging */
public String segString(Directory dir) {
  String cfs;
  try {
    if (getUseCompoundFile())
      cfs = "c";
    else
      cfs = "C";
  } catch (IOException ioe) {
    cfs = "?";
  }

  String docStore;

  if (docStoreOffset != -1)
    docStore = "->" + docStoreSegment;
  else
    docStore = "";

  return name + ":" +
    cfs +
    (this.dir == dir ? "" : "x") +
    docCount + docStore;
}

