public void close() {
  for(Map.Entry<String, SolrClient> entry : solrClients.entrySet()) {
    try {
      entry.getValue().close();
    } catch (IOException e) {
      log.error("Error closing SolrClient for " + entry.getKey(), e);
    }
  }
}

