@Override
public void updateDocument(Term term,
    IndexDocument doc, Analyzer analyzer)
    throws IOException {
  super.updateDocument(term, doc, analyzer);
  try {
    if (waitAfterUpdate) {
      signal.countDown();
      latch.await();
    }
  } catch (InterruptedException e) {
    throw new ThreadInterruptedException(e);
  }
}

