/** Set flushPending if it is not already set and returns
 *  whether it was set. This is used by IndexWriter to *
 *  trigger a single flush even when multiple threads are
 *  * trying to do so. */
synchronized boolean setFlushPending() {
  if (flushPending)
    return false;
  else {
    flushPending = true;
    return true;
  }
}

